import React, { Component } from "react";
import { FirstPara } from "../../assets/beginParagraph";
let time = null;
class Main extends Component {

    constructor(){
        super();
        this.state = {
            startTest: false,
            time: 0,
            testDone: false
        };

    };


    startTest = () => {
        this.setState({startTest: true})
        time = setInterval(() => {
            this.setState({
                time: Math.round((this.state.time + 0.01) * 100) / 100
            });
        }, 10);
    };

    finishTest = () => {
        this.setState({startTest: false, testDone: true});
        clearInterval(time);
    };

    startTrain = () => {
        window.location = "/train";
    }

    render(){
        return(
            <div className="container">
                <div className="jumbotron">
                Faculty of Medicine and Dentistry of University of Alberta.
                Welcome to our program which help increase your reading speed.
                This is the home page!
                {this.state.testDone
                ?
                <div>
                    <p>Your Test Time is: {this.state.time} seconds</p>
                    <button onClick={this.startTrain}>Start Real Training!</button>
                </div>
                :
                <div>
                    {this.state.startTest
                        ?
                        <div>
                            <FirstPara />
                            <button onClick={this.finishTest}>Finish!</button>
                        </div>
                        :
                        <button onClick={this.startTest} >Start Test!</button>
                    }
                </div>
                }

                </div>
            </div>
        )
    }
}

export default Main;
