import React, { Component } from "react";

class QuestionBlock extends Component {

    constructor(props){
        super(props);
        this.state = {
            // questions and answer part need to connect to the databse 
            // and extract the newest data to  this state
            questions:{"Where were the man and the boy traveling?": 
            ["School", "Market", "Hospital", "Park"]},
            answers:["School"],
            youtAnswer: null
        };

    };

    checkAnswer = () => {
        if (this.state.youtAnswer === this.state.answers[0]) {
            this.props.rightAnswer();
        } else {
            this.props.wrongAnswer();
        }
    }

    changeAnswer = (e) => {
        this.setState({ youtAnswer: e.target.value})
    }


    render(){

        return(
            <div>
                <h2>Where were the man and the boy traveling?</h2>
                <p>"School", "Market", "Hospital", "Park"</p>
                <input onChange={this.changeAnswer} />
                <button onClick={this.checkAnswer} >Confirm</button>
            </div>
        )
    }
}

export default QuestionBlock;
