import React, { Component } from "react";
import TrainingBlock from "./TrainingBlock";

class Train extends Component {

    constructor(){
        super();
        this.state = {
            startTrain: false,
            quesNum: 1,
            speed: 2, // default 2s 
        };

    };

    // TODO: connect to the database and extract the user's reading speed
    // according to the speed to determine how much time should the words disappeare.

    startTrain = () => {
        this.setState({ startTrain: true });
    }


    render(){
        return(
            <div className="container">
                <div className="jumbotron">
                    <h1>Welcome to the training page</h1>
                    {
                        this.state.startTrain 
                        ?
                        <TrainingBlock disTime={this.state.speed} />
                        :
                        <button onClick={this.startTrain} >Start Train!</button>
                    }
                </div>
            </div>

        )
    }
}

export default Train;
